package Things;import MyExeption.NoColorException;import java.util.Random;public abstract class Item {    private final String ITEMNAME;    private final Color COLOR;    public Item(String itemName, Color color) {        this.ITEMNAME = itemName;        this.COLOR = color;    }    public String getItemName() {        return ITEMNAME;    }    public Color getColor() {        return COLOR;    }    public static Color RandomColor(int x) throws NoColorException{        Random random = new Random();        int a = random.nextInt(x);        Color color;        switch (a) {            case 1:                color = Color.BLACK;                break;            case 2:                color = Color.RAD;                break;            case 3:                color = Color.BLUE;                break;            case 4:                color = Color.YELLOW;                break;            case 5:                color = Color.BROWN;                break;            case 6:                color = Color.GREEN;                break;            default:                throw new NoColorException();        }        return color;    }    public enum Color{        BLUE ("blue"),        RAD ("rad"),        YELLOW ("yellow"),        GREEN ("green"),        SILVER ("silver"),        BLACK ("black"),        BROWN("brown");        private final String S;        Color (String s){            this.S = s;        }        @Override        public String toString() {            return S;        }    }}